template<typenameT,class=typenamestd::enable_if<isValidVariantType<T,setting_t>::value>::type>
booloperator==(constT&otherSetting){
if(autosettingValue=get_if<T>()){
return*settingValue==otherSetting;
}
returnfalse;
}

/**
*@briefInequalityoperator,counterparttoequalityoperator
*@seeoperator==(constT&otherSetting);
*/
template<typenameT,
class=typenamestd::enable_if<isValidVariantType<T,setting_t>::value>::type>
booloperator!=(constT&setting){
return!(*this==setting);
}

